import React from 'react';
import { useSelector } from 'react-redux';
import './components.css';


function Trendingtopics() {
  const sharedValue = useSelector((state) => state.shared.sharedValue);
  return (
    <div className="trending-stories">
        <div className="text-1">Trending stories</div>
	<div className="user-topics">
          <div className="user-option">Technology<span className="remove">x</span></div>
	  <div className="user-option">Programming<span className="remove">x</span></div>
	</div>
        <div className="stories">
          <div className="story">
            <h6>Climate change</h6>
	    <div className="story-content">Content</div>
          </div>
          <div className="story">
            <h6>Interior design</h6>
	    <div className="story-content">Content</div>
          </div>
        </div>
    </div>
  );
};

export default Trendingtopics;
