const initialState = {
  sharedValue: [],
};


function sharedReducer(state = initialState, action) {
  switch (action.type) {
    case 'SET_SHARED_VALUE':
      state.sharedValue.push(action.payload)
      const newValue = state.sharedValue;
      return { ...state, sharedValue: newValue };
    default:
      return state;
  }
}

export default sharedReducer;
