import React from 'react';
import Navbar from 'react-bootstrap/Navbar';
import Nav from 'react-bootstrap/Nav';
import './components.css';


function Header() {
  return (
    <div className="header">
      <NavBar/>
      <About/>
      <Optionsbox/>
    </div>
  );
}

function About() {
  return (
    <div className="about">
      <div className="about-text">
        <div className="text-1">Discover stories about your favourite topics</div>
      </div>
    </div>
  );
}

function Optionsbox() {
  return ( 
    <div className="options">
      <div className="option">Technology<span className="remove">x</span></div>
      <div className="option">Home Decor<span className="remove">x</span></div>
      <div className="option">Programming<span className="remove">x</span></div>
      <div className="option">Lifestyle<span className="remove">x</span></div>
      <div className="option">Fashion<span className="remove">x</span></div>
      <div className="option">Architecture<span className="remove">x</span></div>
      <div className="option">Business<span className="remove">x</span></div>
    </div>
  );
}

function NavBar() {
  return (
    <div className="navigation navbar-expand-lg navbar-light bg-light .navbar-expand{-sm|-md|-lg|-xl}" bg="light">
      <div className="site-name">World stories</div>
      <Navbar.Toggle aria-controls="basic-navbar-nav" />
      <Navbar.Collapse id="basic-navbar-nav">
        <Nav className="ml-auto header-navigation">
          <Nav.Link className="nav-element" href="/">Home</Nav.Link>
	  <Nav.Link className="nav-element" href="/login">Login</Nav.Link>
	  <Nav.Link className="nav-element" href="/signup">Signup</Nav.Link>
	</Nav>
      </Navbar.Collapse>
    </div>
  );
}

export const header = Header;
export const navBar = NavBar;
