const initialState = {
  Technology: null,
  HomeDecor: null,
  Programming: null,
  Lifestyle: null,
  Fashion: null,
  Architecture: null,
  Business: null,
  Climatechange: null,
};


function sharedReducer(state = initialState, action) {
  if (Object.keys(state).includes(action.type)) {
    const key = action.type;
    return { ...state, key: action.payload };
  }
}

export default sharedReducer;
