const initialState = {
  sharedValue: [],
};


function sharedReducer(state = initialState, action) {
  switch (action.type) {
    case 'SET_SHARED_VALUE':
      return {
        ...state,
	sharedValue: [...state.sharedValue, action.payload],
      }
    default:
      return state;
  }
}

export default sharedReducer;
