import React, { useState } from 'react';
import './components.css';
import { navBar as NavBar } from './header';

function Login() {
  return (
    <div>
      <NavBar/>
      <LoginForm/>
    </div>
  );
}

function LoginForm() {
  const [formData, setFormData] = useState({
    username: '',
    password: '',
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(formData);
  };

  return (
    <div className="signup-form">
      <form onSubmit={handleSubmit}>
        <div className="field">
	  <label htmlFor="username">Username</label>
	  <br/>
	  <input onChange={handleInputChange} autoFocus="autofocus" className="form-control" value={formData.username} type="text" name="username" id="username"/>
	</div>
        <div className="field">
	  <label htmlFor="password">Username</label>
	  <br/>
	  <input onChange={handleInputChange} autoFocus="autofocus" className="form-control" value={formData.password} type="text" name="password" id="password"/>
	</div>
      </form>
    </div>
  );

}

export default Login;
