import './App.css';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import { useNavigate } from "react-router-dom";
import { useEffect, useState } from 'react';
import HomePage from './components/home';
import Login from './components/login';
import Signup from './components/signup';
import Refresh from './components/refresh';
import Story from './components/read_story';
import { AuthProvider, useAuth } from './authentication/auth-provider';

function App() {
  const navigate = useNavigate();
  const [current, setCurrent] = useState('/');
  //const currentLocation = window.location.href;

  useEffect(() => {
    const expires_at = localStorage.getItem('EXPIRES_AT');
    const now = new Date();
    const expire_time = new Date(parseInt(expires_at, 10));
    const diff = (expire_time.getTime() - now.getTime()) / 1000;
    console.log(diff);
    console.log(currentLocation);
    setCurrent(window.location.href);
    if (diff < 1) {
      navigate('/auth/login');
    }
  }, [currentLocation, navigate]);
  return (
    <div className="App">
      <AuthProvider>
        <Routes>
          <Route path="/" element={<HomePage />} />
          <Route path="/login" element={<Login />} />
          <Route path="/signup" element={<Signup />} />
          <Route path="/auth/login" element={<Refresh previous={current}/>} />
          <Route path="/story" element={<Story />} />
        </Routes>
      </AuthProvider>
    </div>
  );
}

export default App;
