const initialState = {
  'Technology': null,
  'Home Decor': null,
  'Programming': null,
  'Lifestyle': null,
  'Fashion': null,
  'Architecture': null,
  'Business': null,
  'Climate change': null,
};


function sharedReducer(state = initialState, action) {
  switch (action.type) {
    case 'SET_SHARED_VALUE':
      return { ...state, sharedValue: action.payload };
    default:
      return state;
  }
}

export default sharedReducer;
